mapper.select:
select r.id, r.role_name as roleName, count(u.id) as users, JSON_LENGTH( r.permissions ) as permissions, r.`desc`, r.systemic from tb_role as r left join tb_user as u on JSON_CONTAINS(u.role, CONVERT(r.id, CHAR)) where 1 = 1
and r.role_name like '%${roleName}%'
group by r.id order by r.id LIMIT #{start}, #{length}

mapper.insert:
insert into tb_role
set role_name = #{roleName},
permissions = #{permissions}

    ,`desc`=#{desc}
